//389. Find The Difference
//You are given two strings s and t.
//String t is generated by random shuffling string s and then add one more letter at a random position.
//Return the letter that was added to t.

function findTheDifference(s, t){
  const countOfS = {};
  const countOfT = {};

  for(let i = 0; i < s.length; i++){
    const currentChar = s.charAt(i);
    countOfS[currentChar] ? countOfS[currentChar]++ : countOfS[currentChar] = 1;
  }
  for(let i = 0; i < t.length; i++){
    const currentChar = t.charAt(i);
    countOfT[currentChar] ? countOfT[currentChar]++ : countOfT[currentChar] = 1;
  }

  for(let key in countOfT){
    if(countOfT[key] !== countOfS[key]){
      return key.toString();
    }
  }
}

console.log(findTheDifference('abcd', 'abcde'));
console.log(findTheDifference('', 'y'));
console.log(findTheDifference('a', 'aa'));
